// SPDX-License-Identifier: MIT OR Apache-2.0

pragma solidity ^0.8.20;

/// @dev copied from https://github.com/nomad-xyz/ExcessivelySafeCall/blob/main/src/ExcessivelySafeCall.sol.
library SafeCall {
    /// @notice calls a contract with a specified gas limit and value and captures the return data
    /// @param _target The address to call
    /// @param _gas The amount of gas to forward to the remote contract
    /// @param _value The value in wei to send to the remote contract
    /// to memory.
    /// @param _maxCopy The maximum number of bytes of returndata to copy
    /// to memory.
    /// @param _calldata The data to send to the remote contract
    /// @return success and returndata, as `.call()`. Returndata is capped to
    /// `_maxCopy` bytes.
    function safeCall(
        address _target,
        uint256 _gas,
        uint256 _value,
        uint16 _maxCopy,
        bytes memory _calldata
    ) internal returns (bool, bytes memory) {
        // check that target has code
        uint size;
        assembly {
            size := extcodesize(_target)
        }
        if (size == 0) {
            return (false, new bytes(0));
        }

        // set up for assembly call
        uint256 _toCopy;
        bool _success;
        bytes memory _returnData = new bytes(_maxCopy);
        // dispatch message to recipient
        // by assembly calling "handle" function
        // we call via assembly to avoid memcopying a very large returndata
        // returned by a malicious contract
        assembly {
            _success := call(
                _gas, // gas
                _target, // recipient
                _value, // ether value
                add(_calldata, 0x20), // inloc
                mload(_calldata), // inlen
                0, // outloc
                0 // outlen
            )
            // limit our copy to 100 bytes
            _toCopy := returndatasize()
            if gt(_toCopy, _maxCopy) {
                _toCopy := _maxCopy
            }
            // Store the length of the copied bytes
            mstore(_returnData, _toCopy)
            // copy the bytes from returndata[0:_toCopy]
            returndatacopy(add(_returnData, 0x20), 0, _toCopy)
        }
        return (_success, _returnData);
    }

    /// @notice Use when you _really_ really _really_ don't trust the called
    /// contract. This prevents the called contract from causing reversion of
    /// the caller in as many ways as we can.
    /// @dev The main difference between this and a solidity low-level call is
    /// that we limit the number of bytes that the callee can cause to be
    /// copied to caller memory. This prevents stupid things like malicious
    /// contracts returning 10,000,000 bytes causing a local OOG when copying
    /// to memory.
    /// @param _target The address to call
    /// @param _gas The amount of gas to forward to the remote contract
    /// @param _maxCopy The maximum number of bytes of returndata to copy
    /// to memory.
    /// @param _calldata The data to send to the remote contract
    /// @return success and returndata, as `.call()`. Returndata is capped to
    /// `_maxCopy` bytes.
    function safeStaticCall(
        address _target,
        uint256 _gas,
        uint16 _maxCopy,
        bytes memory _calldata
    ) internal view returns (bool, bytes memory) {
        // check that target has code
        uint size;
        assembly {
            size := extcodesize(_target)
        }
        if (size == 0) {
            return (false, new bytes(0));
        }

        // set up for assembly call
        uint256 _toCopy;
        bool _success;
        bytes memory _returnData = new bytes(_maxCopy);
        // dispatch message to recipient
        // by assembly calling "handle" function
        // we call via assembly to avoid memcopying a very large returndata
        // returned by a malicious contract
        assembly {
            _success := staticcall(
                _gas, // gas
                _target, // recipient
                add(_calldata, 0x20), // inloc
                mload(_calldata), // inlen
                0, // outloc
                0 // outlen
            )
            // limit our copy to 256 bytes
            _toCopy := returndatasize()
            if gt(_toCopy, _maxCopy) {
                _toCopy := _maxCopy
            }
            // Store the length of the copied bytes
            mstore(_returnData, _toCopy)
            // copy the bytes from returndata[0:_toCopy]
            returndatacopy(add(_returnData, 0x20), 0, _toCopy)
        }
        return (_success, _returnData);
    }
}

contracts/lowlat/EssenceDVNWrapper.sol.content:
// SPDX-License-Identifier: LZBL-1.2

pragma solidity ^0.8.20;

import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";

import { MultiCall } from "./MultiCall.sol";

/// @title EssenceDVNWrapper is a contract, used to execute multiple worker calls in a single transaction
/// The calls should be as follows:
/// 1. N calls to dvn.execute() to verify the payload on ULN
/// 2. 1 call to uln.commitVerification() to commit the verification into the endpoint
/// 3. 1 call to executor.execute() to execute lzReceive() on the endpoint
///
/// Node: The DVN should assign EssenceDVNWrapper as an admin to the DVN contract.
///
contract EssenceDVNWrapper is AccessControl, MultiCall {
    bytes32 internal constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    error DVNWrapper_InvalidRole(bytes32 role);
    error DVNWrapper_InvalidAdminCount();
    error DVNWrapper_RoleRenouncingDisabled();

    uint256 public adminCount;

    modifier onlyAdmin(bytes32 _role) {
        if (_role == ADMIN_ROLE) {
            _checkRole(_role); // admin required
        } else {
            revert DVNWrapper_InvalidRole(_role);
        }
        _;
    }

    constructor(address[] memory admins) {
        if (admins.length == 0) {
            revert DVNWrapper_InvalidAdminCount();
        }
        for (uint i = 0; i < admins.length; i++) {
            _setupRole(ADMIN_ROLE, admins[i]);
        }
        adminCount = admins.length;
    }

    function multiCall(
        Call[] calldata _calls
    ) public payable override onlyAdmin(ADMIN_ROLE) returns (bool[] memory successes, bytes[] memory results) {
        (successes, results) = super.multiCall(_calls);
    }

    // ========================= Override Functions =========================

    function grantRole(bytes32 _role, address _account) public override onlyAdmin(_role) {
        _grantRole(_role, _account);
    }

    function revokeRole(bytes32 _role, address _account) public override onlyAdmin(_role) {
        _revokeRole(_role, _account);
    }

    function renounceRole(bytes32 /*role*/, address /*account*/) public pure override {
        revert DVNWrapper_RoleRenouncingDisabled();
    }

    function _grantRole(bytes32 _role, address _account) internal override {
        if (_role == ADMIN_ROLE) ++adminCount;
        super._grantRole(_role, _account);
    }

    function _revokeRole(bytes32 _role, address _account) internal override {
        if (_role == ADMIN_ROLE) --adminCount;
        if (adminCount == 0) {
            revert DVNWrapper_InvalidAdminCount(); // not allowed to remove all admins
        }
        super._revokeRole(_role, _account);
    }
}

contracts/lowlat/MultiCall.sol.content:
// SPDX-License-Identifier: LZBL-1.2

pragma solidity ^0.8.20;

import { Transfer } from "@layerzerolabs/lz-evm-protocol-v2/contracts/libs/Transfer.sol";

contract MultiCall {
    struct Call {
        address target;
        bytes data;
        uint256 gasLimit;
        uint256 value;
        bool revertOnError;
    }

    error NotEnoughGas(uint256 index, uint256 requested, uint256 available);
    error CallReverted(uint256 index, bytes result);

    function multiCall(
        Call[] calldata _calls
    ) public payable virtual returns (bool[] memory successes, bytes[] memory results) {
        successes = new bool[](_calls.length);
        results = new bytes[](_calls.length);

        for (uint256 i = 0; i < _calls.length; i++) {
            Call calldata call = _calls[i];

            if (gasleft() < call.gasLimit) revert NotEnoughGas(i, call.gasLimit, gasleft());

            (successes[i], results[i]) = call.target.call{
                value: call.value,
                gas: call.gasLimit == 0 ? gasleft() : call.gasLimit
            }(call.data);

            if (!successes[i] && _calls[i].revertOnError) revert CallReverted(i, results[i]);
        }

        if (address(this).balance > 0) {
            Transfer.native(msg.sender, address(this).balance);
        }
    }
}

